import React from 'react';
import './Avatar.css';
import 'tachyons';
const Avatar = (props) => {
	return (
		<div className="avatarstyle ma4 bg-light-blue dib pa4">
		<img src="{props.name}" alt="8nkit"/>
		<h1 className="tc">{props.name} </h1>
		<p> {props.work} </p>
		</div>
		)
}
export default Avatar;
